			select count(1) from 
			(
  				select 
	  				u.PromoCode ,u.Id ,u.userName ,u.registerDate ,u.AccountDate ,u.HasFuiou ,u.SourceType ,
	  				CASE WHEN (u.HasFuiou='0' and u.SourceType='0') THEN '0' ELSE '1' END  AS isKH
				from tb_userInfo u
				where 1=1 

					and u.PromoCode = #{userInfo.promocode}


					  and u.registerDate >= #{userInfo.regBeginTime}     


					  and u.registerDate <= #{userInfo.regEndTime}   


					  and u.AccountDate >= #{userInfo.accountBeginTime}     


					  and u.AccountDate <= #{userInfo.accountEndTime}   

			) a 
			where 1=1 

				  and a.isKH = #{userInfo.isKH}

select * from 
(
	select row_number() OVER (ORDER BY registerDate DESC) n,* from 
	( 
		select u.PromoCode ,u.Id ,u.userName ,u.registerDate ,u.AccountDate ,u.mobile ,u.HasFuiou ,u.SourceType ,CASE WHEN (u.HasFuiou='0' and u.SourceType='0') THEN '0' ELSE '1' END AS isKH 
		from tb_userInfo u 
		where 1=1 	

			and u.PromoCode = #{userInfo.promocode}


			  and u.registerDate >= #{userInfo.regBeginTime}     


			  and u.registerDate <= #{userInfo.regEndTime}   


			  and u.AccountDate >= #{userInfo.accountBeginTime}     


			  and u.AccountDate <= #{userInfo.accountEndTime}   

	)a 
	where 1=1

		  and a.isKH = #{userInfo.isKH}   

)b
where 1=1

	  and n > #{page.start} and n <= #{page.end}  

order by registerDate desc

