SELECT count(1) FROM 
(
	SELECT ROW_NUMBER() OVER(PARTITION BY u.userName ORDER BY u.Id) identityid ,
	u.PromoCode ,u.Id ,u.userName ,u.registerDate ,u.AccountDate ,u.HasFuiou ,u.SourceType ,u.mobile,
	CASE WHEN (u.HasFuiou='0' and u.SourceType='0') THEN '0' ELSE '1' END  AS isKH,b.lendMoney 
	FROM dbo.tb_userInfo u 
	INNER JOIN dbo.tb_lendInfo b ON u.userName = b.userName  
	where 1=1 

		and u.PromoCode = #{userInfo.promocode}


		  and u.registerDate >= #{userInfo.regBeginTime}     


		  and u.registerDate <= #{userInfo.regEndTime}   


		  and u.AccountDate >= #{userInfo.accountBeginTime}     


		  and u.AccountDate <= #{userInfo.accountEndTime}   

) b 
WHERE identityid = 1 AND b.lendMoney >= 1000

	  and b.isKH = #{userInfo.isKH}

  			select * from 
  			(
	  			SELECT ROW_NUMBER() OVER(ORDER BY accountDate DESC)n,* FROM 
				(
					SELECT ROW_NUMBER() OVER(PARTITION BY u.userName ORDER BY u.Id) identityid ,
					u.PromoCode ,u.Id ,u.userName ,u.registerDate ,u.AccountDate ,u.HasFuiou ,u.SourceType ,u.mobile,
					CASE WHEN (u.HasFuiou='0' and u.SourceType='0') THEN '0' ELSE '1' END  AS isKH,b.lendMoney 
					FROM dbo.tb_userInfo u 
					INNER JOIN dbo.tb_lendInfo b ON u.userName = b.userName  
					where 1=1 

						and u.PromoCode = #{userInfo.promocode}


						  and u.registerDate >= #{userInfo.regBeginTime}     


						  and u.registerDate <= #{userInfo.regEndTime}   


						  and u.AccountDate >= #{userInfo.accountBeginTime}     


						  and u.AccountDate <= #{userInfo.accountEndTime}   

				) b 
				where identityid = 1 AND b.lendMoney >= 1000

					  and b.isKH = #{userInfo.isKH}   

  			)bb
  			where 1=1

				  and n > #{page.start} and n <= #{page.end}  

			order by registerDate desc

SELECT * FROM 
(
